class ArrayHandler {
public:
    // Allocate an array dynamically
    std::vector<int> *allocateArray(size_t size)) {
        std::vector<int> *ptr = new std::vector<int>;
        ptr->reserve(size);
        return ptr;
    }

    // Fill an array with numbers
    void fillArray(std::vector<int> *ptr, int value)) {
        ptr->push_back(value);
    }

    // Deallocate the memory properly
    void deallocateArray(std::vector<int> *ptr)) {
        delete ptr;
    }

private:
