#include <iostream>

class ArrayHandler {
private:
    int** array;
    int size;

public:
    // Constructor to initialize array size
    ArrayHandler(int initialSize = 10) {
        size = initialSize;
        array = new int*[size];
        for (int i = 0; i < size; ++i) {
            array[i] = new int[size]; // Allocate memory for each row
        }
    }

    ~ArrayHandler() { // Destructor to deallocate memory properly
        for (int i = 0; i < size; ++i) {
            delete[] array[i]; // Deallocate memory for each row
        }
        delete[] array; // Deallocate memory for all rows
    }

    void fillArray(int value) {
        for (int i = 0; i < size * size; ++i) {
            array[i / size][i % size] = value;
        }
    }

    void printArray() {
        for (int i = 0; i < size; ++i) {
            for (int j = 0; j < size; ++j) {
                std::cout << array[i][j] << " ";
            }
            std::cout << "\n";
        }
    }
};

int main() {
    ArrayHandler handler(5);
    handler.fillArray(1);
    handler.printArray();

    return 0;
}
```

