// Use a map to efficiently store and sum numbers
function addNumbers(arr) {
    const numberMap = new Map();

    // Process the array using `reduce`
    // This method accumulates values in a accumulator object
    let sum = arr.reduce((acc, num)) => {
        // If the number is already in the map,
        // simply update its value
        if (numberMap.has(num)) {
            acc += numberMap.get(num);
        } else { // Add the number to the map with a default value of 0
            numberMap.set(num, 0);
            acc += num;
        }
        return acc;
    }, 0);

    return sum;
}
