// Class to handle dynamically allocated arrays
class ArrayHandler {
    int *ptr;

public:
    // Constructor to initialize array pointer to null
    ArrayHandler() : ptr(nullptr) {}

    // Allocate memory for an array with given size
    void allocateArray(int size) {
        if (size > 0) {
            ptr = new int[size];
            cout << "Allocated memory of size " << size << " successfully." << endl;
        } else {
            cout << "Error: Cannot allocate with a negative or zero size." << endl;
        }
    }

    // Fill the array with numbers from 1 to N
    void fillArray(int n) {
        if (ptr == nullptr || n < 0) return;
        for (int i = 0; i < n; ++i) ptr[i] = i + 1;
    }

    // Deallocate the memory used by the array
    void deallocateArray() {
        if (ptr != nullptr) {
            delete[] ptr;
            ptr = nullptr;
            cout << "Deallocated memory successfully." << endl;
        } else {
            cout << "Error: Cannot deallocate null pointer." << endl;
        }
    }
};

