```c++
// ArrayHandler.h
class ArrayHandler {
public:
  // Allocate an array of size n with default value 0
  void allocate(size_t n);

  // Fill the array with values from 1 to n
  void fill(void);

  // Deallocate the memory for the allocated array
  void deallocate(void);

private:
  int* arr;   // Stores an array of integers (char** is also possible)
  size_t len; // Length of the array
};
```

And here's the implementation in C++:

```c++
// ArrayHandler.cpp
#include "ArrayHandler.h"

void ArrayHandler::allocate(size_t n) {
  arr = new int[n];
  len = n;
}

void ArrayHandler::fill(void) {
  for (int i = 0; i < len; i++)
    arr[i] = i + 1;
}

void ArrayHandler::deallocate(void) {
  delete[] arr;   // Deallocate memory for the array
  arr = nullptr;  // Set 'arr' to null (for safety)
  len = 0;        // Reset the length of the array
}
```

