```
#include <iostream>
#include <cstdlib>

class ArrayHandler {
public:
    // Constructor to allocate memory for the array
    ArrayHandler(int size) {
        data = new int[size];
    }

    // Destructor to deallocate the memory for the array
    ~ArrayHandler() {
        delete[] data;
    }

    // Method to fill the array with numbers
    void fill(int start, int end) {
        for (int i = start; i <= end; i++) {
            data[i] = i * 2;
        }
    }

    // Method to access and modify an element of the array
    int get(int index) {
        return data[index];
    }

    // Method to deallocate the memory for the array
    void deallocate() {
        delete[] data;
    }

private:
    int* data;
};

int main() {
    ArrayHandler handler(10);
    handler.fill(0, 9);
    std::cout << "Array: ";
    for (int i = 0; i < 10; i++) {
        std::cout << handler.get(i) << " ";
    }
    std::cout << std::endl;
    handler.deallocate();
    return 0;
}
```
