private:
    int* arr;  // Pointer to dynamically allocated memory

public:
    // Allocate memory for an array of given size
    void allocateArray(int size) {
        if (size > 0) {  // Check if size is valid
            arr = new int[size];  // Dynamic allocation using 'new' operator
        } else {
            std::cout << "Invalid array size!" << std::endl;
        }
    }

    // Fill the array with given numbers
    void fillArray(int num, int count) {
        if (arr != nullptr && count > 0) {  // Check memory allocation and validity of count
            for (int i = 0; i < count; i++) {  // Loop to fill array
                arr[i] = num;
            }
        } else {
            std::cout << "Memory allocation error or invalid count!" << std::endl;
        }
    }

    // Deallocate the dynamically allocated memory properly
    void deallocateArray() {
        if (arr != nullptr) {  // Check if memory is allocated
            delete[] arr;  // Use 'delete[]' to properly deallocate dynamic array
            arr = nullptr;  // Reset pointer to avoid accidental usage
        } else {
            std::cout << "Memory not allocated!" << std::endl;
        }
    }
};

int main() {
    ArrayHandler handler;

    // Allocate an array of size 5 and fill it with numbers
    handler.allocateArray(5);
    handler.fillArray(10, 5);  // Fill the first 5 elements with number 10

    // Check the filled array
    for (int i = 0; i < 5; i++) {
