function sortStrings(array) {
  // Define a comparator function that compares two strings based on their ASCII values
  function compareStrings(a, b) {
    // Get the length of each string
    let lenA = a.length;
    let lenB = b.length;

    // If one string is longer than the other, return -1 if it is longer and 1 if it is shorter
    if (lenA > lenB) {
      return 1;
    } else if (lenA < lenB) {
      return -1;
    }

    // Loop through each character of the strings and compare them
    for (let i = 0; i < Math.min(lenA, lenB); i++) {
      // Get the ASCII value of each character and subtract it from 64 if it is an uppercase letter or 96 if it is a lowercase letter
      let charA = a[i];
      let charB = b[i];
      let asciiA = charA.charCodeAt(0) - 64;
      let asciiB = charB.charCodeAt(0) - 96;

      // If the ASCII values are not equal, return -1 if the first string has a larger value and 1 if it has a smaller value
      if (asciiA !== asciiB) {
        return asciiA - asciiB;
      }
    }

    // If the strings have the same length and all their characters are equal, return 0
    return 0;
  }

  // Sort the array using the compareStrings function as a criterion
  return array.sort(compareStrings);
}

