#include <iostream>
#include <cstdlib> // for free()

class ArrayHandler {
public:
  // constructor that takes the size of the array as an argument
  ArrayHandler(int size) : size_(size), data_(nullptr) {}

  // method to allocate the array dynamically and store the pointer in data_
  void alloc() {
    // use malloc() to get a block of memory from the system
    data_ = (int*) malloc(size_ * sizeof(int));
    // check for NULL pointer and throw an exception if not
    if (data_ == nullptr) {
      throw std::runtime_error("Failed to allocate memory");
    }
  }

  // method to fill the array with numbers from 1 to size()
  void fill() {
    for (int i = 0; i < size_; ++i) {
      data_[i] = i + 1; // add 1 to get the numbers from 1 to size()
    }
  }

  // method to deallocate the memory and set data_ to null
  void free() {
    free(data_); // use free() to give back the memory to the system
    data_ = nullptr;
  }

  // method to print the contents of the array
  void print() const {
    for (int i = 0; i < size_; ++i) {
      std::cout << data_[i] << " ";
    }
    std::cout << std::endl;
  }

  // getter method for the size of the array
  int getSize() const { return size_; }

private:
  // the size of the array
  int size_;

  // the pointer to the data
  int* data_;
};

