            arr=new int[n];  /* Allocating dynamic memory to arr */
        }
        void fillArr(int n){
            for (int i = 0; i < n ;i++) {     //loop to initialize values in array
                *arr = rand()%20+1;    /* Initializing random values into the array */ 
                arr += 1;   /* Moving pointer to next address */                
            }
        }

        void deAllocArr(){
             delete [] this->arr ; //deallocating dynamic memory to the arr variable
        }
};//class ArrayHandler ends here
