    int n;   /* Declaring integer variable for array size */
    cout<<"Enter the size of the array: ";  cin>>n;     //asking for input from user
    ArrayHandler *a1 = new ArrayHandler();      ///creating pointer variable
    a1->allocateArr(n);   /* Calling allocate() method to allocate dynamic memory */
    a1->fillArr(n);       //Calling fill() method to fill the array with random values 
    cout<<"\nThe elements of the array are:\n";      //printing the array before printing the sum
    for (int i = 0;i<n ; ++i){
