#include <iostream>

using namespace std;

class ArrayHandler {
public:
    int* allocateArray(int size) {
        return new int[size];
    }

    void fillArray(int* arr, int size) {
        for (int i = 0; i < size; i++) {
            arr[i] = i + 1;
        }
    }

    void deallocateArray(int* arr) {
        delete[] arr;
    }
};

int main() {
    ArrayHandler handler;
    int size = 10;

    int* arr = handler.allocateArray(size);
    handler.fillArray(arr, size);

    for (int i = 0; i < size; i++) {
        cout << arr[i] << endl;
    }

    handler.deallocateArray(arr);

    return 0;
}
```

