#include "ArrayHandler.h"

class ArrayHandler {
public:
    void addNumber(int index, int value) {
        if (index < 0 || index > size()) {
            throw new out_of_range("Index out of range");
        }

        // Check if the array is allocated dynamically and initialized with default values
        if (array == nullptr) {
            array = new int[size() + 1];

            for (int i = 0; i < size(); i++) {
                array[i] = DEFAULT_VALUE;
            }
        }

        // If the index is valid, update the value at that index in the array
        if (index >= 0 && index <= size()) {
            array[index] = value;
        }
    }

