    void allocate(int size) {
        if (size <= 0) {
            std::cerr << "Invalid size for allocation." << std::endl;
            return;
        }

        // Allocate memory using 'new' operator
        pointer_ = new int[size];

        // Check if memory was allocated successfully
        if (pointer_ == nullptr) {
            std::cerr << "Memory allocation failed." << std::endl;
        }
    }

    // Method to fill the array with numbers
    void fill(int* arr, int size, int value) {
        for (int i = 0; i < size; ++i) {
            arr[i] = value; // Assign value to each element
        }
    }

    // Method to deallocate the dynamically allocated memory properly
    void deallocate() {
        delete[] pointer_; // Use 'delete[]' to deallocate multidimensional arrays

        pointer_ = nullptr;
    }

private:
    int* pointer_; // Pointer to the dynamically allocated array
};
