public:
    // Constructor to allocate memory for the array
    ArrayHandler(int size) {
        // Use malloc to allocate memory for the array
        data = (int*)malloc(size * sizeof(int));
        if (data == nullptr) {
            std::cerr << "Error: Could not allocate memory for array" << std::endl;
