/**
 * Reverses the input string and displays a Toast message with the reversed string.
 * @param str The input string to be reversed.
 */
fun reverseString(str: String) {
    // Create a new empty string to store the reversed characters
    var revStr = ""

    for (i in str.length - 1 downTo 0) {
        // Add each character of the input string to the new string in reverse order
        revStr += str[i]
    }

    // Display a Toast message with the reversed string
    Toast.makeText(context, revStr, Toast.LENGTH_SHORT).show()
}
```

