public:
    explicit ArrayHandler(int size) : size_(size), array_(new int[size]) {}

    ~ArrayHandler() {
        delete[] array_;
    }

    void fillArray(int start, int step) {
        for (int i = 0; i < size_; ++i) {
            array_[i] = start + (step * i);
        }
    }

    void displayArray() const {
        std::cout << "Array: [";
        for (int i = 0; i < size_; ++i) {
            std::cout << array_[i];
            if (i != size_ - 1) {
                std::cout << ", ";
            }
        }
        std::cout << "]" << std::endl;
    }

private:
    int size_;
    int* array_;
};

int main() {
    ArrayHandler handler(10);
    handler.fillArray(1, 2);
    handler.displayArray();

    return 0;
}
```

