using namespace std;

class ArrayHandler {
public:
    // default constructor that does nothing
    ArrayHandler() : size_(0), data_(nullptr) {}

    // copy constructor that does nothing
    ArrayHandler(const ArrayHandler& other) : size_(other.size_), data_(other.data_) {}

    // assignment operator that does nothing
    ArrayHandler& operator=(const ArrayHandler& other) {
        if (this != &other) {
            release();
            allocate(other.size_);
        }
        return *this;
    }

    // method to allocate an array dynamically with size as an argument
    void allocate(int size) {
        release(); // free the old memory if any
        data_ = static_cast<int*>(::malloc(size * sizeof(int))); // cast malloc to correct type
        size_ = size; // store the size
    }

    // method to deallocate the memory of the array
    void release() {
        if (data_ != nullptr) {
            ::free(data_); // free the memory
            data_ = nullptr; // reset the pointer
            size_ = 0; // reset the size
        }
    }

    // method to fill the array with numbers from 1 to size
    void fill() {
        for (int i = 0; i < size_; ++i) {
            data_[i] = i + 1; // assign each element the value of its index plus one
        }
    }

    // method to access the element at index i
    int get(int i) const {
        return data_[i];
    }

private:
    int size_; // the size of the array
    int* data_; // the pointer to the array
};

