 This is a C++ program demonstrating dynamic memory allocation and deallocation using pointers. It creates a class 'ArrayHandler' that handles all tasks related to array manipulation using dynamic memory management:

```c++
#include <iostream>
using namespace std;

class ArrayHandler {
public:
    // Constructor initializes pointer and size
    ArrayHandler(int capacity) {
        data = new int[capacity];
        this->capacity = capacity;
        this->size = 0;
    }

    // Destructor to deallocate memory when the object is destroyed
    ~ArrayHandler() {
        delete[] data;
    }

    // Methods for array manipulation using dynamic memory allocation and deallocation
    void allocate(int capacity) {
        if (capacity > size) {
            int *temp = new int[capacity];
            for (int i = 0; i < size; ++i)
                temp[i] = data[i];

            delete[] data;
            data = temp;
            capacity = capacity;
        }
    }

    void fillWithNumbers(int index, int number) {
        if (index >= 0 && index < size) {
            data[index] = number;
        } else {
            cout << "Error: Index out of range" << endl;
        }
    }

    void deallocate() {
        delete[] data;
        capacity = 0;
        size = 0;
    }

private:
    int *data;
    int capacity, size;
};

int main() {
    ArrayHandler arrayHandler(5);

    cout << "Original capacity is " << arrayHandler.capacity << endl;
    arrayHandler.allocate(7);
    cout << "New allocated capacity is " << arrayHandler.capacity << endl;

    for (int i = 0; i < arrayHandler.capacity; ++i) {
        if (i < arrayHandler.size) {
            int number = rand() % 100 + 1;
            cout << "Enter a number to fill at index " << i << ": ";
            cin >> number;
            arrayHandler.fillWithNumbers(i, number);
        } else {
            cout << "No number for this index" << endl;
        }
    }
    cout << "Array filled: " << endl;
    for (int i = 0; i < arrayHandler.capacity; ++i) {
        if (arrayHandler.data[i])
            cout << arrayHandler.data[i] << ' ';
        else
            cout << "_ ";
    }
    cout << endl;

    // Increasing capacity to 10 and filling with numbers
    cout << "Increasing capacity to 10" << endl;
    arrayHandler.allocate(10);
    for (int i = 9; i < 15; ++i) {
        int number = rand() % 100 + 1;
        cout << "Enter a number to fill at index " << i << ": ";
        cin >> number;
        arrayHandler.fillWithNumbers(i, number);
    }
    cout << "Array after increasing capacity: " << endl;
    for (int i = 0; i < arrayHandler.capacity; ++i) {
        if (arrayHandler.data[i])
            cout << arrayHandler.data[i] << ' ';
        else
            cout << "_ ";
    }
    cout << endl;

    // Deallocate memory and destroy object
    arrayHandler.deallocate();
    return 0;
}
```

