    void fill(int* arr, size_t count, int num) {
        for (size_t i = 0; i < count; ++i) {
            arr[i] = num;
        }
    }

    // Method to deallocate the dynamically allocated memory properly
    void deallocate(void* ptr) {
        if (ptr != nullptr) {
            std::free(ptr);
            ptr = nullptr;
        }
    }
};

int main() {
    ArrayHandler ah;

    // Allocate an array of 10 integers
    int* arr = ah.allocate(sizeof(int)) * 10);
    if (arr == nullptr) {
