Here's a sample implementation of a C++ program that demonstrates dynamic memory allocation and deallocation using pointers, along with a class named `ArrayHandler`:
```cpp
#include <iostream>
#include <cstring>
using namespace std;
class ArrayHandler {
private:
    int* dynamicallyAllocatedArray;
public:
    ArrayHandler() {
        dynamicallyAllocatedArray = nullptr;
    }

    void allocateArray(int size) {
        if (dynamicallyAllocatedArray != nullptr) {
            cout << "Deallocating previous memory..." << endl;
            delete[] dynamicallyAllocatedArray;
        }
        dynamicallyAllocatedArray = new int[size];
        if (dynamicallyAllocatedArray == nullptr) {
            cerr << "Memory allocation failed!" << endl;
            return;
        }
        cout << "Memory allocated successfully. Size: " << size << " bytes" << endl;
    }

    void fillArray(int size, int value) {
        for (int i = 0; i < size; ++i) {
            dynamicallyAllocatedArray[i] = value;
        }
    }

    void deallocateArray() {
        if (dynamicallyAllocatedArray != nullptr) {
            cout << "Deallocating dynamically allocated memory..." << endl;
            delete[] dynamicallyAllocatedArray;
            dynamicallyAllocatedArray = nullptr;
        } else {
            cout << "No memory to deallocate." << endl;
        }
    }

    void displayArray() {
        if (dynamicallyAllocatedArray != nullptr) {
            for (int num : *dynamicallyAllocatedArray) {
                cout << num << " ";
            }
            cout << endl;
        } else {
            cout << "No array allocated." << endl;
        }
    }
};

int main() {
    ArrayHandler arrayHandler;

    int size = 5; // Change this size as needed
    arrayHandler.allocateArray(size);

    const int fillValue = 42;
    arrayHandler.fillArray(size, fillValue);

    cout << "Dynamic Array Contents: ";
    arrayHandler.displayArray();

    arrayHandler.deallocateArray();

    return 0;
}
```
