        dist[i] = INT_MAX, sptSet[i] = false;

    // Distance of source vertex from itself is always zero
    dist[src] = 0;

    // Find shortest path for all vertices using the above function (or Dijkstra's method)
    int u;
    for (int count = 0; count < V - 1; count++) {
        // Pick the minimum distance vertex from the set of vertices not yet processed. u is always equal to src in first iteration.
        u = minDistance(dist, sptSet);

        sptSet[u] = true;

        // Update dist value of the adjacent vertices of the picked vertex.
        for (int v = 0; v < V; v++)

            // Update dist[v] only if is not in sptSet, there is an edge from u to v, and total weight of path from src to v through u is smaller than current fastest
            // path.
            if (!sptSet[v] && graph[u][v] && dist[u] != INT_MAX && dist[u] + graph[u][v] < dist[v])
                dist[v] = dist[u] + graph[u][v];
    }
}

// Driver code to test above functions
int main()
{
    // An undirected weighted graph G. This graph contains two islands which are connected by three bridges
    int graph[][V] = {
        {0, 2, 0, 6, 0, 9, 8, 0, 0},
        {2, 0, 3, 8, 5, 10, 3, 0, 0},
        {0, 3, 0, 0, 7, 0, 4, 0, 2},
        {6, 8, 0, 0, 9, 14, 0, 0, 0},
        {0, 5, 7, 9, 0, 15, 11, 0, 0},
        {9, 10, 0, 14, 15, 0, 6, 8, 0},
        {8, 3, 4, 0, 11, 6, 0, 9, 14},
        {0, 0, 0, 0, 0, 8, 9, 0, 7},
        {0, 0, 2, 0, 0, 0, 14, 7, 0}};

    dijkstra(graph, 0);

    printSolution(dist, V);

    return 0;
