class ArrayHandler {
public:
  // This is a dummy data type that will be used to store the elements of the array
  typedef int Element;

  // This is a function that takes an integer n as a parameter and returns an array of size n+1
  static Element* allocate(int n) {
    // We use the operator new to dynamically allocate memory for an array of size n+1
    // We pass the size n as a template argument to the operator new
    // We add 1 to the size because the array needs one more element to store the zero-fill value
    Element* ptr = ::new(static_cast<void*>(nullptr)) Element[n+1];

    // We use the operator delete to free the memory when we are done with the array
    // We pass the same pointer that we received from the operator new
    // We add 1 to the pointer because the operator delete expects the address of the first element in the array
    ::delete(static_cast<Element*>(nullptr), ptr);

    return ptr;
  }

  // This is a function that takes an array and a fill value as parameters and fills the array with the fill value
  static void fill(Element* arr, int value) {
    // We use a loop to iterate over the elements of the array
    for (int i = 0; i < arr->size(); ++i) {
      // We assign the fill value to the element at index i
      arr[i] = value;
    }
  }

  // This is a function that takes an array as a parameter and deallocates the memory properly
  static void deallocate(Element* arr) {
    // We use the operator delete to free the memory for the array
    ::delete arr;
  }
};

