// Function to reverse a string
fun reverseString(input: String): String {
  // Initialize an empty string to store the reversed string
  var reversed = ""

  // Loop through each character in the input string
  for (i in 0 until input.length) {
    // Append the character to the reversed string as a modifier
    reversed += input[input.length - i - 1]
  }

  // Return the reversed string
  return reversed
}

// Function to display a Toast message with the reversed string
fun showReversedString(reversed: String) {
  // Use the Toast class to show the message
  Toast.makeText(this, reversed, Toast.LENGTH_SHORT).show()
}

