    // Add or update the expense in the appropriate category
    if (!budgetData.expensesMap[category]) {
        budgetData.expensesMap[category] = []
    }
    budgetData.expensesMap[category].append(new Expense(category, amount))

    // Update the total spent in each category and adjust allocations if necessary
    updateBudgetAllocations(budgetData)
}

// Function to suggest initial budget allocation based on categories
