        budgetData.budgetAllocations[category] = monthlyIncome * (suggestedPercentage / 100)
    }
}

// Function to update the budget allocations based on actual expenses
function updateBudgetAllocations(budgetData) {
    for (category, expenses in budgetData.expensesMap.items()) {
        totalSpent = sum(expense.amount for expense in expenses)

        // Adjust the allocation based on actual spending vs. suggested percentage
        if (totalSpent > budgetData.budgetAllocations[category]) {
            print("Warning: You've exceeded your budget for " + category)
        } else {
            remainingBudget = budgetData.budgetAllocations[category] - totalSpent

            // Optionally, redistribute the unspent amount to other categories or savings
            if (remainingBudget > 0) {
                print("You have " + remainingBudget + " left in your budget for " + category)
            }
        }
    }
}

