#include <iostream>
using namespace std;

class ArrayHandler {
public:
    int* createAndFillArray(int size) {
        // Allocate memory for the array using dynamic allocation
        int* myArray = new (nothrow) int[size];

        if (myArray == nullptr) {
            cout << "Memory allocation failed." << endl;
            return nullptr; // Return nullptr if allocation fails
        }

        // Fill the array with numbers from 1 to size
        for (int i = 0; i < size; ++i) {
            myArray[i] = i + 1;
        }

        return myArray;
    }

    void deleteArray(int* arr, int size) {
        // Deallocate the memory using dynamic deallocation
        if (arr != nullptr) {
            delete[] arr;
            cout << "Memory for array has been freed." << endl;
        } else {
            cout << "No memory to free." << endl;
        }
    }
};

int main() {
    ArrayHandler handler;

    // Define the size of the array
    int arraySize = 10;

    // Create and fill the array dynamically
    int* myArray = handler.createAndFillArray(arraySize);

    if (myArray != nullptr) {
        // Print the elements of the array
        for (int i = 0; i < arraySize; ++i) {
            cout << "Element at index " << i << ": " << myArray[i] << endl;
        }

        // Deallocate the memory after use
        handler.deleteArray(myArray, arraySize);
    } else {
        cout << "Failed to create and fill the array." << endl;
    }

    return 0;
}
