#include <iostream>
using namespace std;

class ArrayHandler {
private:
    int* arr; // Pointer to dynamic memory
    int size; // Size of the allocated array

public:
    // Constructor to allocate memory dynamically
    ArrayHandler(int initialSize) {
        if (initialSize > 0) {
            size = initialSize;
            arr = new int[size]; // Dynamic memory allocation
            if (arr == nullptr) {
                cout << "Memory allocation failed!" << endl;
                size = 0; // Reset size to avoid leaks
            }
        } else {
            cout << "Invalid initial size for dynamic array." << endl;
        }
    }

    // Method to fill the array with numbers
    void fillArray(int num) {
        if (size > 0) {
            for (int i = 0; i < size; i++) {
                arr[i] = num; // Fill array with given number
            }
        } else {
            cout << "Array is empty, cannot fill it." << endl;
        }
    }

    // Method to deallocate the dynamic memory
    void deallocateMemory() {
        if (size > 0) { // If memory was allocated
            delete[] arr; // Deallocate memory using 'delete[]'
            size = 0; // Reset size to avoid leaks
        } else {
            cout << "Array is already deallocated." << endl;
        }
    }

    // Method to display the array
    void displayArray() {
        if (size > 0) {
            for (int i = 0; i < size; i++) {
                cout << arr[i] << " "; // Display each element in the array
            }
            cout << endl;
        } else {
            cout << "Array is empty." << endl;
        }
    }
};

// Main function to demonstrate dynamic memory allocation and deallocation
int main() {
    ArrayHandler ah(5); // Create an array handler with initial size 5

    // Fill the array with numbers
    ah.fillArray(10);

    // Display the array
    ah.displayArray();

    // Deallocate the memory
    ah.deallocateMemory();

    return 0;
}
```

