```kotlin
fun reverseAndShowToast(inputText: String) {
    // Create a StringBuilder object to store the reversed string
    val reversedStringBuilder = StringBuilder(inputText)

    // Reverse the input string by iterating through it and swapping characters at the beginning and end
    for (i in 0 until inputText.length / 2) {
        val temp = reversedStringBuilder[i]
        reversedStringBuilder[i] = reversedStringBuilder[inputText.length - i - 1]
        reversedStringBuilder[inputText.length - i - 1] = temp
    }

    // Convert the StringBuilder back to a String and display it in a Toast message
    val reversedString = reversedStringBuilder.toString()
    Toast.makeText(this, "Reversed string: $reversedString", Toast.LENGTH_SHORT).show()
}
