// Function to reverse a user's input string and show it in a Toast
fun reverseAndShowInput(context: Context, inputString: String) {
    // Check if the input is not null or empty
    if (inputString.isNotEmpty()) {
        // Reverse the input string using the reverse() function
        val reversedString = inputString.reversed()

        // Create a Toast object to display messages
        val toast = Toast.makeText(context, reversedString, Toast.LENGTH_SHORT)

        // Set up the view that should be displayed in the Toast
        toast.view = context.layoutInflater.inflate(R.layout.toast_reverse_layout, null)

        // Find the TextView in the inflated layout to display the reversed string
        val textView: TextView = toast.view.findViewById(R.id.textViewReversed)

        // Set the text of the TextView to the reversed string
        textView.text = reversedString

        // Show the Toast with the reversed string
        toast.show()
    } else {
        // If the input is empty, show a different message or handle accordingly
        val emptyInputToast = Toast.makeText(context, "Please enter some text to reverse.", Toast.LENGTH_SHORT)
        emptyInputToast.show()
    }
}

