class ArrayHandler {
private:
    int* arr; // Pointer for dynamic allocation of integer array elements
    size_t capacity; // Capacity to hold integers in the dynamically allocated array

public:
    // Constructor that initializes an empty array with a given capacity.
    ArrayHandler(size_t initCapacity) : capacity(initCapacity), arr(nullptr) {}

    ~ArrayHandler() {
        delete[] arr; // Destructor to deallocate the memory of 'arr' when object is destroyed
    }

    void allocateAndFill(int valueToFill = 0) {
        if (capacity == 0) throw std::runtime_error("Array capacity must be greater than zero.");

        // Dynamically allocating an array with the given initial capacity.
        arr = new int[capacity];

        for (size_t i = 0; i < capacity; ++i) {
            arr[i] = valueToFill; // Filling each element of 'arr' with a specified integer value or default if not provided
        }
    }

    void printArray() const {
        for (size_t i = 0; i < capacity; ++i) {
            cout << arr[i] << " "; // Printing each element of 'arr' followed by a space.
        }
        cout << endl;
    }
};

int main() {
    ArrayHandler handler(10); // Creating an instance with initial capacity for 10 integers

    try {
        handler.allocateAndFill(); // Allocating memory and filling the array with zeros (default value)

        cout << "Array after allocation: ";
        handler.printArray(); // Printing filled array elements to verify correctness of 'allocateAndFill' method
    } catch(const std::runtime_error& e) {
