```cpp
#include <iostream>

class ArrayHandler {
private:
    int* arr;
    int size;

public:
    // Constructor to allocate memory dynamically
    ArrayHandler(int n) : size(n) {
        arr = new int[size];
        std::cout << "Memory allocated for an array of size " << size << "\n";
    }

    // Destructor to deallocate memory
    ~ArrayHandler() {
        delete[] arr;
        std::cout << "Memory deallocated\n";
    }

    // Method to fill the array with numbers from 1 to n
    void fillArray() {
        for (int i = 0; i < size; ++i) {
            arr[i] = i + 1;
        }
    }

    // Method to display the contents of the array
    void display() const {
        std::cout << "Array elements: ";
        for (int i = 0; i < size; ++i) {
            std::cout << arr[i] << " ";
        }
        std::cout << "\n";
    }
};

int main() {
    int n;
    std::cout << "Enter the size of the array: ";
    std::cin >> n;

    // Create an instance of ArrayHandler
    ArrayHandler ah(n);

    // Fill the array with numbers
    ah.fillArray();

    // Display the contents of the array
    ah.display();

    return 0;
}
```

