#include <iostream>
using namespace std;

class ArrayHandler {
private:
    int* arr; // Pointer to dynamic array of integers
    size_t capacity; // Capacity of the dynamically allocated array

public:
    // Constructor that allocates memory for an initial capacity and sets it up with zeros.
    ArrayHandler(size_t initCapacity) : capacity(initCapacity), arr(new int[capacity]) {
        std::fill_n(arr, capacity, 0);
    }

    // Destructor that deallocates the memory for 'arr'.
    ~ArrayHandler() {
        delete[] arr;
    }

    void fillWithNumbers(size_t count) {
        if (count > capacity) throw std::overflow_error("Count exceeds array's capacity.");

        // Fill the allocated memory with numbers from 1 to 'count'.
        for (size_t i = 0; i < count; ++i) {
            arr[i] = static_cast<int>(i + 1);
        }
    }

    void printArray() const {
        // Print the array elements.
        cout << "Array contents: ";
        for (size_t i = 0; i < capacity; ++i) {
            cout << arr[i] << ' ';
        }
        cout << '\n';
    }
};

int main() {
    ArrayHandler handler(10); // Create an instance with initial capacity of 10.

    try {
        handler.fillWithNumbers(5); // Fill the array with numbers from 1 to 5.
        cout << "Array after filling: ";
        handler.printArray();

        // The destructor will be called automatically when 'handler' goes out of scope, deallocating memory for arr.
    } catch (const std::overflow_error& e) {
        cerr << "Error: " << e.what() << endl;
    }

    return 0; // The destructor will be called automatically here as well to clean up the allocated array.
}
