class BudgetTool:
    def __init__(self):
        self.categories = {'Essentials': 0, 'Savings': 0, 'Discretionary Spending': 0}
        self.expenses = {}

    def input_income(self):
        while True:
            try:
                monthly_income = float(input('Enter your monthly income: '))
                if monthly_income < 0:
                    print('Income cannot be negative. Please enter a valid amount.')
                else:
                    break
            except ValueError:
                print('Invalid input. Please enter a number.')
        self.distribute_income(monthly_income)

    def distribute_income(self, income):
        suggested_percentages = {'Essentials': 50, 'Savings': 30, 'Discretionary Spending': 20}
        for category in self.categories:
            self.categories[category] = income * (suggested_percentages[category] / 100)

    def input_expenses(self):
        while True:
            category = input('Enter expense category (Essentials/Savings/Discretionary Spending) or "done" to finish: ').capitalize()
            if category == 'Done':
                break
            elif category not in self.categories:
                print('Invalid category. Please enter a valid category.')
                continue
            while True:
                try:
                    expense = float(input('Enter the amount of your expense: '))
                    if expense < 0:
                        print('Expense cannot be negative. Please enter a valid amount.')
                    else:
                        break
                except ValueError:
                    print('Invalid input. Please enter a number.')
            self.expenses[category] = self.expenses.get(category, 0) + expense

    def generate_report(self):
        total_spent = sum(self.expenses.values())
        income = sum(self.categories.values())
        print('\nSpending Report:')
        for category in self.categories:
            print(f'{category}: {self.expenses.get(category, 0)} out of {self.categories[category]} ({self.expenses.get(category, 0) / self.categories[category] * 100}%)')
        print(f'Total spent: {total_spent} out of {income} ({total_spent / income * 100}%)')

def main():
