class ArrayHandler {
private:
    int* arr; // Pointer for dynamic allocation of integer array elements
public:
    void allocateArray(int size) {
        if (size <= 0) throw invalid_argument("Size must be positive");

        // Dynamically allocating memory using new operator and storing the pointer in 'arr'
        arr = new int[size];
    }

    void fillArray() {
        for(int i = 0; i < sizeof(*this)/sizeof(int); ++i) {
            this->arr[i] = static_cast<int>(rand()) % (100 - 'a' + 1) + 'a'; // Filling with random lowercase letters a-z
        }
    }

    void deallocateArray() {
        delete[] arr; // Deallocating memory using the pointer stored in `arr`
        arr = nullptr; // Setting the pointer to NULL after deletion for safety reasons (avoid dangling pointers)
    }
};

int main() {
    ArrayHandler handler(10);  // Creating an instance of 'ArrayHandler' with size 10.

    try {
        handler.allocateArray(handler.size());  

        cout << "Filling the array..." << endl;
        handler.fillArray();

        for (int i = 0; i < sizeof(*this)/sizeof(int); ++i) { // Printing elements of allocated memory block to verify correctness
            cout << this->arr[i] << " ";
        }
        cout << endl;

    } catch (const invalid_argument& e) {
        cerr << "Error: " << e.what() << endl; // Exception handling for negative size input
    }

    handler.deallocateArray();  // Properly deallocating the memory to avoid leaks and undefined behavior

    return 0;
}
