        if (data_) {
            delete[] data_;
            data_ = nullptr;
        }
    }

    void allocateAndFill() {
        if (!data_) {
            data_ = new int[size_];
            for (int i = 0; i < size_; ++i) {
                data_[i] = i * i;
            }
        }
    }

    void printArray() const {
        if (data_) {
            for (const int& num : *this) {
                std::cout << num << ' ';
            }
            std::cout << '\n';
        } else {
            std::cout << "No array allocated.\n";
        }
    }

    int& operator[](int index) {
        if (!data_) {
            allocateAndFill();
        }
        return data_[index];
    }

private:
    int* data_{nullptr};
    int size_{0};
};

int main() {
